function doSmooth(){
    var i = 10;
    var int = setInterval(function() {
        window.scrollTo(0,0);
        i += 10;
        if (i >= 200) clearInterval(int);
    }, 20);
    return false;
}
    //TIMING OUT ALERTS -       OK
function doSetTimeout(n, t = 2500) {
    doSmooth();
    setTimeout(function(){
        n.className += ' fadeout';
    }, t);

    setTimeout(function() { 
        var nParent =  n.parentNode;
        if(nParent){
            nParent.removeChild(n);
        }
    }, t + 500);
}
